import React, { Component } from 'react';
import {Navbar, NavDropdown, Nav} from 'react-bootstrap';
import 'react-bootstrap';
import './Welcome.css';
import hackerman from './Images/hackerman2.jpg';
import logo from './Images/logoharmaa.png';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import './JoinForm.css';
import { InputGroupCheckbox } from 'react-bootstrap/InputGroup';
import { format } from 'path';
import AdminView from './AdminView';




class AdminViewTapahtuma1 extends Component {




  state = {
    startTime: '',
    endTime: '',
    missa: '',
    mika: '',
    linkki:'',
    kuva: '',
    teksti: '1',
    hinta:''
    
};


validate = () => {
  let isError = false;
 

  if (this.state.startTime.length <1) {
    isError = true;
   
  }
  if (this.state.endTime.length <1) {
    isError = true;
  
  }
  if (this.state.missa.length <1) {
    isError = true;
  
  }
  if (this.state.mika.length <1) {
    isError = true;
   
  }
  if (this.state.hinta.length <1) {
    isError = true;
   
  }
  
if(isError){
  this.setState({
    ...this.state,
   
  });
}

  return isError;
};





toggleChange = () => {
  if(this.state.tiedotus==='0'){
    this.setState({
      tiedotus: '1',
      
    });
  //  console.log("nyt 1")
  }
    else if(this.state.tiedotus==='1') {
      this.setState({
        tiedotus: '0',
      });
    //  console.log("nyt 0")
    }
  }



change = e => {
  this.setState({
    [e.target.name]: e.target.value
  });
  console.log("hyhysdaysd")
};  





onSubmit = e => {
  e.preventDefault();
  // this.props.onSubmit(this.state);
  const err = this.validate();
if(!err){
  (async () => {
    const rawResponse = await fetch('http://localhost:8000/tapahtumat/', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({start_time: this.state.startTime, end_time: this.state.endTime , missa: this.state.missa, mika: this.state.mika, linkki:this.state.linkki, kuva:this.state.kuva, teksti:this.state.teksti, hinta:this.state.hinta})
    
    }
    
    );
    window.location.reload(true)
    const content = await rawResponse.json();
    
    
  })();

  this.setState({
    startTime: '',
    endTime: '',
    missa: '',
    mika: '',
    linkki:'',
    kuva: '',
    teksti: '1',
    hinta:''
 
  });
};
}

  render() {


    
    return (
        <div class ="FormStyle">
            <br></br>
<Form>
  <Form.Row>
    <Form.Group as={Col} controlId="formGridFirstName">
      <Form.Label ><span>Aloituspvm</span></Form.Label>
      <Form.Control type="text" placeholder="Aloituspvm (YYYY-MM-DD-HH-MM-SS)" value ={this.state.startTime}
        onChange={e => this.change(e)}
        name = "startTime"
        
        />
    </Form.Group>

  </Form.Row>
  <Form.Row>

  <Form.Group as={Col} controlId="formGridLastName">
      <Form.Label>Lopetuspvm</Form.Label>
      <Form.Control type="string" placeholder="Lopetuspvm (YYYY-MM-DD-HH-MM-SS)" value ={this.state.lastName}
        onChange={e => this.change(e)}
        name = "lastName" />
        
    </Form.Group>
    </Form.Row>


  <Form.Row>
    <Form.Group as={Col} controlId="formGridHomeTown">
      <Form.Label>Kotipaikkakunta</Form.Label>
      <Form.Control type="string" placeholder="Joensuu" value ={this.state.kotipaikka}
        onChange={e => this.change(e)}
        name = "kotipaikka" />
    </Form.Group>
    </Form.Row>

    <Form.Row>

    <Form.Group as={Col} controlId="formGridEmail">
      <Form.Label>Sähköposti</Form.Label>
      <Form.Control type="email" placeholder="forssa@gmail.com" value ={this.state.email}
        onChange={e => this.change(e)}
        name = "email" />
    </Form.Group>
  </Form.Row>

  <Form.Row>
  <Form.Group controlId="formGridType">
    <Form.Label>Jäsentyyppi</Form.Label>
    <Form.Control as="select"  onChange={e => this.change(e)} value={this.state.jasentyyppi} name='jasentyyppi'>
      <option value ={'1'}  name = "jasentyyppi1" >Opiskelijajäsen</option>
      <option value ={'2'}  name = "jasentyyppi2"  >Kannatusjäsen</option>
      <option value ={'3'}  name = "jasentyyppi3"  >Yhteisöjäsen</option>
    </Form.Control>




  </Form.Group>  
  </Form.Row>
    <Form.Row>


    <Form.Group as={Col} controlId="formGridStartYear">
      <Form.Label>Opintojen aloitusvuosi</Form.Label>
      <Form.Control type="number" placeholder="2020" value ={this.state.aloitusvuosi}
        onChange={e => this.change(e)}
        name = "aloitusvuosi" />
      
    </Form.Group>

</Form.Row>




  <Form.Group id="formGridCheckbox1">
    <Form.Check type="checkbox" defaultChecked={true} onChange={this.toggleChange} label="Haluan liittyä Skripti ry:n postituslistalle." />
  </Form.Group>
  <Form.Group id="formGridCheckbox2">
    <Form.Check type="checkbox" label="Hyväksyn jäsenrekisterin ehdot ja vakuutan tietoni oikeiksi * "/>
    <button type ="submit" id = "submitButton" onClick={e => this.onSubmit(e)}>ADD</button>
  </Form.Group>
 





  
</Form>
<br></br>

</div>);

  }
}


export default AdminViewTapahtuma1;
