import React, { Component } from 'react';
import {Navbar, NavDropdown, Nav} from 'react-bootstrap';
import 'react-bootstrap';
import './Welcome.css';
import hackerman from './Images/hackerman2.jpg';
import logo from './Images/logoharmaa.png';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import './JoinForm.css';
import { InputGroupCheckbox } from 'react-bootstrap/InputGroup';




class JoinForm extends Component {




  state = {
    firstName: '',
    lastName: '',
    kotipaikka: '',
    email: '',
    aloitusvuosi: '',
    tiedotus: ''
};


toggleChange = () => {

  if(checkbox.checked ==true){
    this.setState({
      tiedotus: '1',
    });

  }else{
    this.setState({
      tiedotus: '0',
    });
  }

}


change = e => {
  this.setState({
    [e.target.name]: e.target.value
  });
  console.log("hyhysdaysd")
};  

onSubmit = e => {
  e.preventDefault();
  // this.props.onSubmit(this.state);


  (async () => {
    const rawResponse = await fetch('http://localhost:8000/jasenrekisteri/', {
      method: 'POST',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({firstName: this.state.firstName, lastName: this.state.lastName , kotipaikka: this.state.kotipaikka, email: this.state.email, jasentyyppi:55, aloitusvuosi:this.state.aloitusvuosi, tiedotus:this.state.tiedotus})
    });
    const content = await rawResponse.json();


  })();

  this.setState({
    firstName: '',
    lastName: '',
    kotipaikka: '',
    email: '',
    aloitusvuosi: '',
    tiedotus: ''

  });
};

  render() {

    return (
        <div class ="FormStyle">
            <br></br>
<Form>
  <Form.Row>
    <Form.Group as={Col} controlId="formGridFirstName">
      <Form.Label ><span>Etunimi</span></Form.Label>
      <Form.Control type="text" placeholder="Ville" value ={this.state.firstName}
        onChange={e => this.change(e)}
        name = "firstName"/>
    </Form.Group>

  </Form.Row>
  <Form.Row>

  <Form.Group as={Col} controlId="formGridLastName">
      <Form.Label>Sukunimi</Form.Label>
      <Form.Control type="string" placeholder="Kärkkänen" value ={this.state.lastName}
        onChange={e => this.change(e)}
        name = "lastName" />
    </Form.Group>
    </Form.Row>


  <Form.Row>
    <Form.Group as={Col} controlId="formGridHomeTown">
      <Form.Label>Kotipaikkakunta</Form.Label>
      <Form.Control type="string" placeholder="Joensuu" value ={this.state.kotipaikka}
        onChange={e => this.change(e)}
        name = "kotipaikka" />
    </Form.Group>
    </Form.Row>

    <Form.Row>

    <Form.Group as={Col} controlId="formGridEmail">
      <Form.Label>Sähköposti</Form.Label>
      <Form.Control type="email" placeholder="forssa@gmail.com" value ={this.state.email}
        onChange={e => this.change(e)}
        name = "email" />
    </Form.Group>
  </Form.Row>

  <Form.Row>
  <Form.Group controlId="formGridType">
    <Form.Label>Jäsentyyppi</Form.Label>
    <Form.Control as="select">
      <option>Opiskelijajäsen</option>
      <option>Kannatusjäsen</option>
      <option>Yhteisöjäsen</option>
    </Form.Control>




  </Form.Group>  
  </Form.Row>
    <Form.Row>


    <Form.Group as={Col} controlId="formGridStartYear">
      <Form.Label>Opintojen aloitusvuosi</Form.Label>
      <Form.Control type="number" placeholder="2020" value ={this.state.aloitusvuosi}
        onChange={e => this.change(e)}
        name = "aloitusvuosi" />
    </Form.Group>

</Form.Row>






  <Form.Group id="formGridCheckbox">
    <Form.Check type="checkbox" checked={this.state.tiedotus} onChange={this.toggleChange} label="Haluan liittyä Skripti ry:n postituslistalle." />
  </Form.Group>
  <Form.Group id="formGridCheckbox">
    <Form.Check type="checkbox"  label="Hyväksyn jäsenrekisterin ehdot ja vakuutan tietoni oikeiksi * " />
  </Form.Group>



  <button id = "submitButton" onClick={e => this.onSubmit(e)}>ADD</button>
</Form>
<br></br>

</div>);

  }
}


export default JoinForm;
